import dbconnection from "../../../../database/connection.js";

const connection = dbconnection();

// 1- add product.
export const addProduct = (req, res, next) => {
  connection.query("INSERT INTO products SET ?", req.body, (error, result) => {
    if (error) {
      return res.status(500).json({ message: "Database error", error });
    }

    res.status(201).json({ message: "Product added successfully", result });
  });
};
//2- API to find the total revenue generated by each category of products.
export const getSumByCategory = (req, res, next) => {
  const { category } = req.body;

  if (!category) {
    return res.status(400).json({ message: "Category is required" });
  }
  connection.query(
    "SELECT SUM(unitPrice) AS Total_Price FROM `products` WHERE category = ?",
    [category],
    (error, results) => {
      if (error) {
        return res.status(500).json({ message: "Database error", error });
      }

      res.status(200).json(results[0]);
    }
  );
};
//3- API to find the total number of items sold for each product.
export const getTotalItemsSold = (req, res, next) => {
  const { productName } = req.body;
  if (!productName) {
    return res.status(400).json({ message: "productName is required" });
  }
  connection.query(
    "SELECT COUNT(productName) AS Total_Product_Sold  FROM `products` WHERE productName='TV';",
    [productName],
    (error, result) => {
      if (error) {
        return res.status(500).json({ message: "Database error", error });
      }
      res.status(200).json(result[0]);
    }
  );
};
